var ATT_OP_WRITE_RESP               = 0x13;
var ATT_OP_PREPARE_WRITE_REQ        = 0x16;
var ATT_OP_PREPARE_WRITE_RESP       = 0x17;
var ATT_OP_EXECUTE_WRITE_REQ        = 0x18;
var ATT_OP_EXECUTE_WRITE_RESP       = 0x19;
